mapper.select:
select n.id,m.mobile,n.company,n.tax,n.ope_user,n.money,n.status,n.email,n.remark,n.ct,n.ut
from invoice n left join merchant m on n.merchant_id=m.shop_id
where n.ope_user in(select child_user_id from relation_user where main_user_id=#{userId})

    and m.mobile = #{query.mobile}


    and m.company like concat('%',#{query.shop},'%')


    and m.status = #{query.status}

order by ct DESC
limit #{query.index},#{query.size}

