mapper.select:
select m.mobile,m.shop,m.ope_user,a.status,m.introduce,a.ct,a.ut,a.remark
from `merchant_audit` a
left join `merchant` m on a.merchant_id=m.shop_id
where a.ope_user = #{opeUserId}

    and a.status = #{status}


    and m.mobile = #{mobile}


    and m.shop like CONCAT('%',#{shop},'%')

limit #{index},#{size}

select count(*)
from `merchant_audit` a
left join `merchant` m on a.merchant_id=m.shop_id
where a.ope_user = #{opeUserId}

    and a.status = #{status}


    and m.mobile = #{mobile}


    and m.shop like CONCAT('%',#{shop},'%')

mapper.update:
update `merchant_audit`
set status = #{status},ut = now()

    ,remark = #{reason}

where merchant_id=#{shopId}
